<?phpdefined('BASEPATH') OR exit('No direct script access allowed');include_once(dirname(BASEPATH).'/inherit/BaseController.php');class Form extends BaseController {    /**     * constructor.     */    function __construct()    {        //父类        parent::__construct();        //环境变量设置        $this->_controller->views = "sell/form/Form";        $this->_controller->layout = "layout/amaze/main";        //类库        $this->load->library('evon/ApiResult','','apiresult');        //加载模型        $this->load->model('sell/form/Form_model',"model",true);        $this->load->model('sell/client/Client_model',"MClient",true);    }    /**     * index     */    public function index()    {        $model = $this->model;        $param = $_REQUEST;        $page = isset($param["page"])?$param["page"]:1;        $size = isset($param["size"])?$param["size"]:20;        $condition = isset($param["condition"])?(array)json_decode($param["condition"]):[];        $sort = isset($param["sort"])?(array)json_decode($param["sort"]):[$model->getPk()=>"ASC"];        $result = $model->search($page,$size,$condition,$sort);        $this->show("list",[            "searched"=>$result,            "page"=>$page,            "size"=>$size,        ]);    }    /**     * index     */    public function index2()    {        $model = $this->model;        $param = $_REQUEST;        $page = isset($param["page"])?$param["page"]:1;        $size = isset($param["size"])?$param["size"]:20;        $condition = isset($param["condition"])?(array)json_decode($param["condition"]):[];        $sort = isset($param["sort"])?(array)json_decode($param["sort"]):[$model->getPk()=>"ASC"];        $result = $model->search($page,$size,$condition,$sort);        $this->show("index",[            "searched"=>$result,            "page"=>$page,            "size"=>$size,        ]);    }    /**     * create     */    public function create(){        $model = $this->model;        $param = $_REQUEST;        if (!empty($param) && $model->load($param) && $model->save()) {            redirect($this->_controller->views."/index");        } else {            $this->show("create",[                "model"=>$model,            ]);        }    }    /**     * update     */    public function update($id){        $model = $this->model->get($id);        $param = $_REQUEST;        if (!empty($param) && $model->load($param) && $model->save()) {            redirect($this->_controller->views."/index");        } else {            $this->show("update",[                "model"=>$model,            ]);        }    }    /**     * delete     */    public function delete($id){        $model = $this->model->get($id);        $bool = $model->delete();        if($bool)            redirect($this->_controller->views."/index");    }    /**     * view     */    public function view($id){        $model = $this->model->get($id);        var_dump($model);    }    /**     * 添加订单     */    public function add(){        $model = $this->model;        $param = $_REQUEST;        $paymentMap = $this->model->getPaymentMap();        if (!empty($param) && $model->load($param) && $model->save()) {            redirect($this->_controller->views."/index");        } else {            $this->show("add",[                "model"=>$model,                "paymentMap"=>$paymentMap            ]);        }    }    /**     * VUE查询接口     */    public function search_api(){        //查询        $model = $this->model;        $param = $_REQUEST;        $start_date = isset($param["start_date"])?$param["start_date"]:null;        $end_date = isset($param["end_date"])?$param["end_date"]:null;        $sort = isset($param["sort"])?(array)json_decode($param["sort"]):[$model->getPk()=>"ASC"];        //设置时区        date_default_timezone_set('Asia/Shanghai');        //拼凑查询条件        $condition = [];        if($start_date)            $condition["create_at >"] = strtotime($start_date);        if($end_date)            $condition["create_at <="] = strtotime($end_date)+86400;        //查询        $result = $model->searchAll($condition,$sort);        //遍历处理        $list = [];        foreach($result->list as $item){            //获取客户            $client = $this->MClient->get($item->client_id);            //添加数据            $item->client = $client;            $item->status_name = $item->getStatusName();            $item->date = date("Y/m/d",$item->create_at);            //加入列表            $list[] = $item;        }        //输出        $this->apiresult->sentApiSuccess($list);    }    /** 添加订单接口 */    public function add_api(){        //参数检测        $this->apiresult->checkApiParameter(['user_id','client_id',"total_num","total_price","payment"],-1);        $param = $_REQUEST;        $param["status"] = 0;        //设置模型        $model = $this->model;        //事务开启        $bool = $model->add($param);        //输出结果        if($bool)            $this->apiresult->sentApiSuccess();        else            $this->apiresult->sentApiError(-1,"fail");    }}