<?phpinclude_once(dirname(BASEPATH).'/inherit/BaseModel.php');class OrderSku_model extends BaseModel{    /**     * @var table     */    protected $table = "t_sell_order_sku";    protected $pk = "id";    /**     * @fields     */    public $id,$order_id,$order_spu_id,$sku_id,$color,$size,$num;    /**     * 检测Sku是否下过单     */    public function checkSkuExist($sku_id){        $query = $this->db->select('COUNT(*) AS `Fnums`')            ->where(array("Fsku_id"=>$sku_id))->get($this->table);;        $result = $query->row();        $count = $result->nums;        if($count > 0)            return true;        else            return false;    }}?>