<?phpinclude_once(dirname(BASEPATH).'/inherit/BaseModel.php');class OrderSpu_model extends BaseModel{    /**     * @var table     */    protected $table = "t_sell_order_spu";    protected $pk = "id";    /**     * @fields     */    public $id,$order_id,$spu_id,$snap_price,$snap_pic,$snap_pic_normal,$remark;    /**     * 检测Spu是否下过单     */    public function checkSpuExist($spu_id){        $query = $this->db->select('COUNT(*) AS `Fnums`')            ->where(array("Fspu_id"=>$spu_id))->get($this->table);;        $result = $query->row();        $count = $result->nums;        if($count > 0)            return true;        else            return false;    }}?>