<?phpinclude_once(dirname(BASEPATH).'/inherit/BaseModel.php');class RefundItem_model extends BaseModel{    /**     * @var table     */    protected $table = "t_sell_refund_item";    protected $pk = "id";    /**     * @fields     */    public $id,$order_id,$refund_id,$order_spu_id,$order_sku_id,$spu_id,$sku_id,$num,$status;    public function __construct()    {        parent::__construct();        $this->load->model('sell/allocate/AllocateItem_model',"allocaiteItem_model",true);    }    /**     * @return object     */    static function describe(){        $data = (object)array();        $data->desc = "RefundItem";        $data->name = "RefundItem";        return $data;    }    /**     * @return array     */    static function attributeLabels()    {        return [];    }    /**     * 获取退貨状态,返回key-value     * @param $order_id     * @return array     */    public function getRefundStatus($order_id, $filterRefundId = null){        //定义条件        $select = "order_sku_id";        $select_sum = "num";        $condition = array("order_id"=>$order_id);        $groupby = array("order_sku_id");        //添加配货单号过滤        if($filterRefundId)            $condition["refund_id !="] = $filterRefundId;        //转义        $select_esc = $this->addPrefixField($select);        $select_sum_esc = $this->addPrefixField($select_sum);        $condition = $this->addPrefixKeyValue($condition);        $groupby = $this->addPrefixFields($groupby);        //查询已经配数量        $query = $this->db            ->select($select_esc)            ->select_sum($select_sum_esc)            ->where($condition)            ->group_by($groupby)            ->get($this->table);        $list = $query->result();        //重整理成key/value形式        $result = array();        foreach($list as $item){            $result[$item->{$select}] = $item->{$select_sum};        }        //返回        return $result;    }    /**     * @return string     */    public function getStatusName(){        switch($this->status){            case 0:                return "未配货";            case 1:                return "完成配货";            default:                return "部分配货";        }    }    /**     * 获取已配数量     */    public function getSendNum(){        $result = $this->allocaiteItem_model->select_sum("send_num",["order_id"=>$this->order_id,"sku_id"=>$this->sku_id]);        if($result)            return (int)$result[0]->send_num;        else            return 0;    }}?>    